INSERT INTO text (source_id, source_table, field_name, lang, content) VALUES (21, 'generic_element', 'naslov', 'sr', 'ISTORIJSKI ARHIV ZRENjANIN I SARADNjA SA MUZEJEM ŽRTAVA GENOCIDA');
INSERT INTO text (source_id, source_table, field_name, lang, content) VALUES (21, 'generic_element', 'opis', 'sr-Cyrl', 'У сали Градског већа Зрењанина уприличено је свечано потписивање Протокола о сарадњи између установа културе Града Зрењанина, Историјског архива Зрењанин и Музеја жртава геноцида у Београду.
Протокол су у присуству градоначелника Зрењанина Симе Салапуре, потписали директори шест установа културе Града Зрењанина, међу којима  и директорка Историјског архива Зрењанина Мирјана Баста.
Протокол је потписао са стране ове референтне установе и директор Музеја жртава геноцида Дејан Ристић.');
INSERT INTO text (source_id, source_table, field_name, lang, content) VALUES (21, 'generic_element', 'opis', 'sr', 'U sali Gradskog veća Zrenjanina upriličeno je svečano potpisivanje Protokola o saradnji izmedju ustanova kulture Grada Zrenjanina, Istorijskog arhiva Zrenjanin i Muzeja žrtava genocida u Beogradu.
Protokol su u prisustvu gradonačelnika Zrenjanina Sime Salapure, potpisali direktori šest ustanova kulture Grada Zrenjanina, medju kojima  i direktorka Istorijskog arhiva Zrenjanina Mirjana Basta.
Protokol je potpisao sa strane ove referentne ustanove i direktor Muzeja žrtava genocida Dejan Ristić.');
